import { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { increment } from '../actions';

function Account() {
  const [account, setAccount] = useState({ amount: 0 });
  const [value, setValue] = useState(0);

  const dispatch = useDispatch()
  const { accountVal} = useSelector(state=>state?.account?.amount)

  const incrementAccountAmount = () => {
    dispatch(increment())
  };

  const decrement = () => {
    setAccount({ amount: account.amount - 1 });
  };

  const incrementByAmount = (value) => {
    setAccount({ amount: account.amount + value });
  };

  return (
    <div className="card">
      <div className="container">
        <h4>
          <b>Account Component</b>
        </h4>
        <h3>Amount:${accountVal}</h3>
        <button onClick={incrementAccountAmount}>Increment +</button>
        <button onClick={decrement}>Decrement -</button>
        <input type="text" onChange={(e) => setValue(Number.parseInt(e.target.value))}></input>
        <button onClick={() => incrementByAmount(value)}>
          Increment By {value} +
        </button>
      </div>
    </div>
  );
}

export default Account;
