import './App.css';
import Account from './components/Account';
import Bonus from './components/Bonus';
function App({store}) {
  console.log(store.getState())

  const accountBal = store?.getState().account.amount
  const bonusPt = store?.getState().bonus.bonus
  return (
    <div className="App">
      <h4>App</h4>
      <h3>Current Amount : {accountBal}</h3>
      <h3>Total Bonus : {bonusPt}</h3>

      <Account></Account>
      <Bonus></Bonus>
    </div>
  );
}

export default App;
