// import { useState } from 'react';
import { useDispatch, useSelector } from "react-redux";
import { decrementBonus, incrementBonus } from "../slices/bonusSlice";
import { useAddAccountMutation, useGetAllUsersQuery, useGetUserByIdQuery } from "../api/adminSlice";

function Admin() {
  const { data, isLoading } = useGetUserByIdQuery(2);
  console.log(data?.amount);
  
  const [addAcccount, response] =  useAddAccountMutation()
  console.log(response)
  
  //Get all Users
  const { data : userData , isLoading : userLoading } = useGetAllUsersQuery()
  console.log(userData);

  return (
    <div className="card">
      <div className="container">
        {/* <h4>
          <b>Admin Component</b>
          {isLoading ? <h2>Loading ... </h2> : <h2>{data?.amount}</h2>}
        </h4> */}
        <h4>
          <b>All Users</b>
          {userLoading ? <h2>Loading ... </h2> : <h2>{userData?.map(user=>(
            <h4 key={user.id}>{user.amount}</h4>
          ))}</h2>}
        </h4>
        <button onClick={()=>addAcccount(7001,4)}>Add New User</button>
      </div>
    </div>
  );
}

export default Admin;
