import { useState } from 'react';
import { useDispatch , useSelector } from 'react-redux';
import { decrementAmount, incrementAmount } from '../slices/accountSlice';

function Account() {
  const [account, setAccount] = useState({ amount: 0 });
  const [value, setValue] = useState(0);

  const dispatch = useDispatch()
  const { account : acc } = useSelector(state=>state)
  console.log(acc)


  return (
    <div className="card">
      <div className="container">
        <h4>
          <b>Account Component</b>
        </h4>
        <h3>Amount:${acc.amount}</h3>
        <button onClick={()=>dispatch(incrementAmount())}>Increment +</button>
        <button onClick={()=>dispatch(decrementAmount())}>Decrement -</button>
        <input type="text" onChange={(e) => setValue(+e.target.value)}></input>
        {/* <button onClick={() => incrementByAmount(value)}> */}
          Increment By {value} +
        {/* </button> */}
      </div>
    </div>
  );
}

export default Account;
